import React, { useState } from 'react';
import axios from 'axios';
import { Card, ListGroup } from 'react-bootstrap';

function UserInfoPage() {
    const [userInfo, setUserInfo] = useState({});

    const showUserInfo = async () => {
        try {
            const response = await axios.get(
                '/api/user_info/',
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('access_token')}`
                    }
                }
            );
            console.log(response.data);
            setUserInfo(response.data)
        } catch (error) {
            console.error('Error creating room:', error);
        }
    };

    return (
        <Card>
          <Card.Body>
            <Card.Title>User Information</Card.Title>
            <ListGroup variant="flush">
              <ListGroup.Item>Email: {userInfo.email}</ListGroup.Item>
              <ListGroup.Item>Username: {userInfo.username}</ListGroup.Item>
              <ListGroup.Item>
                Avatar: <img src={userInfo.avatar} alt="Avatar" style={{ borderRadius: '50%', width: '50px', height: '50px' }} />
              </ListGroup.Item>
              <ListGroup.Item>Address: {userInfo.address}</ListGroup.Item>
              <ListGroup.Item>Phone: {userInfo.phone}</ListGroup.Item>
            </ListGroup>
          </Card.Body>
        </Card>
      );
}

export default UserInfoPage;
