import axios from 'axios';

const loginApi =  (username, password) => {
  try {
    const response = axios.post("http://127.0.0.1:8000/api/login/", { username, password });
    return response.data; // Trả về dữ liệu từ phản hồi, không phải toàn bộ response
  } catch (error) {
    console.error("Error during login:", error);
    throw error;
  }
};

export { loginApi };
