import React from 'react';
import { useState } from 'react';

import { FaPaperPlane, FaSmile } from 'react-icons/fa';
import Picker from "emoji-picker-react";


function MessageInput({ message, handleMessageChange, handleSendMessage, handleMessageKeyPress }) {
  const [chosenEmoji, setChosenEmoji] = useState(null);
  const [isPickerOpen, setIsPickerOpen] = useState(false);

  const handleOpenEmoji = (event, emojiObject) => {
    setChosenEmoji(emojiObject);
  };

  const togglePicker = () => {
    setIsPickerOpen(!isPickerOpen);
  };

  return (
    <div className="chat-message clearfix">
      <div className="input-group-prepend">
        <span className="input-group-text" onClick={handleSendMessage}>
          <FaPaperPlane />
        </span>
        <span onClick={togglePicker}><FaSmile/></span> {/* Biểu tượng emoji */}
        {isPickerOpen && <Picker onEmojiClick={handleOpenEmoji} />} {/* Hiển thị Picker nếu isPickerOpen là true */}
        {chosenEmoji && ( // Hiển thị biểu tượng đã chọn
          <div>
            Your Emoji:
            <img style={{ width: '15px' }} src={chosenEmoji.emoji} alt="emoji" />
          </div>
        )}
      </div>
      <div className="input-group mb-0">
        <textarea
          maxLength={100000}
          type="text"
          className="form-control"
          placeholder="Enter text here..."
          value={message}
          onChange={handleMessageChange}
          onKeyPress={handleMessageKeyPress}
        />
      </div>
    </div>
  );
}

export default MessageInput;
