import React, { useEffect, useState } from 'react';
import './style.css';
import { Link } from 'react-router-dom';
import { loginApi } from '../services/UserServices';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        let access_token = localStorage.getItem('access_token');
        if (access_token) {
            navigate('/home');
        }
    }, [navigate]);

    const handleLogin = async () => {
        try {
            if (!username || !password) {
                console.log('Không có tên người dùng hoặc mật khẩu');
                return;
            }

            const response = await loginApi(username, password);

            console.log(response.data);

            if (response.status === 200 && response.data && response.data.access_token) {
                localStorage.setItem('access_token', response.data.access_token);
                console.log('Access token saved to localStorage:', response.data.access_token);
                // Redirect to home page or handle login success here
            } else {
                console.log('Không có dữ liệu hoặc không có access_token');
                // Handle login failure here
            }
        } catch (error) {
            console.error('Lỗi khi đăng nhập:', error);
            console.log('Có lỗi xảy ra');
            // Handle error here
        }
    };

    return (
        <div className="login template d-flex justify-content-center align-items-center vh-100 bg-primary">
            <div className="form_container p-5 rounded bg-white">
                <form>
                    <h3 className="text-center">Sign In</h3>
                    <div className="mb-2">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            placeholder="Enter Username"
                            className="form-control"
                            value={username}
                            onChange={(event) => setUserName(event.target.value)}
                        />
                    </div>
                    <div className="mb-2">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            placeholder="Enter Password"
                            className="form-control"
                            value={password}
                            onChange={(event) => setPassword(event.target.value)}
                        />
                    </div>
                    <div>
                        <input type="checkbox" className="custom-control custom-checkbox" id="check" />
                        <label htmlFor="check" className="custom-input-label ms-2">
                            Remember me
                        </label>
                    </div>
                    <div className="d-grid">
                        <button className="btn btn-primary" onClick={() => handleLogin()}>
                            Sign in
                        </button>
                    </div>
                    <p className="text-end mt-2">
                        Forgot <a href="">Password?</a>
                        <Link to="/signup" className="ms-2">
                            Sign up
                        </Link>
                    </p>
                </form>
            </div>
        </div>
    );
}

export default Login;
