import React from 'react';
import { ListGroup } from 'react-bootstrap';

function MessageList({ messageList, user_id, username, avatar }) {
  return (
    <div className="chat-history">
      <ListGroup style={{ maxHeight: '500px', overflowY: 'auto' }}>
        {messageList.map(message => (
          <ListGroup.Item key={message.id} className={`clearfix ${message.sender === user_id ? 'chat-message-right mb-4' : 'chat-message-left pb-4'}`}>
            <div>
              <img src={message.avatar ? `http://127.0.0.1:8000/media/${message.avatar}` : 'https://bootdey.com/img/Content/avatar/avatar2.png'} className="rounded-circle mr-1" alt="Avatar" width="40" height="40" />
              <div className="text-muted small text-nowrap mt-2">{message.formatted_time}</div>
            </div>
            <div className={`flex-shrink-1 bg-light rounded py-2 px-3 ${message.sender === user_id ? 'mr-3' : 'ml-3'}`}>
              <div className="font-weight-bold mb-1">{message.sender === user_id ? 'You' : message.username}</div>
              {message.is_block !== 1 ? (
                <div>{message.content}</div>
              ) : (
                <div className="blocked-message">Bạn không thể xem tin nhắn này</div>
              )}
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
}

export default MessageList;
