import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

function UserInfoPage() {
    const [userInfo, setUserInfo] = useState({});
    const [address, setAddress] = useState(userInfo.address);
    const [phone, setPhone] = useState(userInfo.phone);
    const [newAvatar, setNewAvatar] = useState(null);

    const handleSave = () => {
        // Xử lý lưu thông tin người dùng
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        setNewAvatar(file);
    };

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const response = await axios.get(`/api/user_info/`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('access_token')}`
                    }
                });
                setUserInfo(response.data);
                console.log(userInfo)
            } catch (error) {
                console.error('Error fetching user info:', error);
            }
        };

        fetchUserInfo();
    }, []);

    return (
        <Container>
            <Row className="justify-content-center">
                <Col xs={12} md={8}>
                    <h1 className="text-center">User Information</h1>
                    <div className="text-center">
                        <img src={userInfo.avatar} alt="Avatar" style={{ width: '100px', height: '100px', borderRadius: '50%' }} />
                        <h3>{userInfo.username}</h3>
                    </div>
                    <Form>
                        <Form.Group className="mb-3" controlId="formAddress">
                            <Form.Label>Address</Form.Label>
                            <Form.Control type="text" placeholder={userInfo.address} value={address} onChange={(e) => setAddress(e.target.value)} />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formPhone">
                            <Form.Label>Phone</Form.Label>
                            <Form.Control type="text" placeholder={userInfo.phone} value={phone} onChange={(e) => setPhone(e.target.value)} />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formAvatar">
                            <Form.Label>Choose new avatar</Form.Label>
                            <Form.Control type="file" onChange={handleAvatarChange} />
                        </Form.Group>
                        <Button variant="primary" onClick={handleSave}>
                            Save
                        </Button>
                    </Form>
                </Col>
            </Row>
        </Container>
    );
}

export default UserInfoPage;
