import axios from 'axios';

const loginApi = (username, password) => {
  return axios.post("http://127.0.0.1:8000/api/login/", { username, password })
    .then(response => {
      console.log(response.data)
      return response.data;
    })
    .catch(error => {
      console.log(error)
      throw error;
    });
};

export { loginApi };
