import React, { useContext, useEffect } from 'react';
import { useState } from 'react';
import './style.css';
import { FaSearch, FaCircle, FaCamera, FaImage, FaCogs, FaQuestion, FaPaperPlane } from 'react-icons/fa';
import { UserContext } from '../context/Usercontext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import { ListGroup } from 'react-bootstrap';

function Home() {
    const [search, setSearch] = useState('');
    const { logout } = useContext(UserContext);
    const navigate = useNavigate();
    const [chatList, setChatList] = useState([]);
    const [selectedChatId, setSelectedChatId] = useState(null);
    const [messageList, setMessageList] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResult] = useState([]);
    const [message, setMessage] = useState('');
    const access_token = jwtDecode(localStorage.getItem('access_token'))
    const user_id = access_token.user_id
    const [roomInfo, setRoomInfo] = useState(null);

    const handleMessageChange = (e) => {
        setMessage(e.target.value);
    };

    const handleShowRoomInfo = async (room_id) => {
        try {
            const response = await axios.get(`http://127.0.0.1:8000/api/get_room_info/${room_id}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('access_token')}`
                }
            });
            setRoomInfo(response.data);
            console.log(roomInfo)
        } catch (error) {
            console.error('Error fetching room info:', error);
        }
    }

    const handleSendMessage = async () => {
        if (!message.trim()) return; // Không gửi tin nhắn nếu nội dung là rỗng

        try {
            const response = await axios.post(
                'http://127.0.0.1:8000/api/chat/',
                {
                    room_id: selectedChatId,
                    content: message // Gửi nội dung tin nhắn
                },
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('access_token')}`
                    }
                }
            );
            console.log('Message sent:', response.data);
            // Xử lý khi tin nhắn được gửi thành công
        } catch (error) {
            console.error('Error sending message:', error.response.data);
            // Xử lý khi gặp lỗi khi gửi tin nhắn
        }

        // Sau khi gửi tin nhắn, xóa nội dung tin nhắn trong ô nhập liệu
        setMessage('');
    };

    const handleMessageKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    const handleSearch = async () => {
        try {
            const response = await axios.get(`http://127.0.0.1:8000/api/search_user/?search=${searchQuery}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('access_token')}`
                }
            });
            setSearchResult(response.data);
            console.log('Search result:', response.data);
        } catch (error) {
            console.error('Error searching user:', error);
        }
    };

    const handleChangeSearch = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    const handleClick = () => {
        handleSearch();
    };

    useEffect(() => {
        const fetchChatList = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/list_chat/', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('access_token')}`
                    }
                });
                setChatList(response.data);
            } catch (error) {
                console.error('Error fetching chat list:', error);
                if (error.response && error.response.status === 401) {
                    logout();
                    navigate("/");
                }
            }
        };

        fetchChatList();
    }, [logout, navigate]);

    const fetchMessages = async (roomId) => {
        try {
            const response = await axios.get(`http://127.0.0.1:8000/api/message/${roomId}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('access_token')}`
                }
            });
            setMessageList(response.data);
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };
    const handleLogout = () => {
        logout()
        navigate("/");
    }
    const handleChooseRoom = (roomId) => {
        setSelectedChatId(roomId);
        fetchMessages(roomId);
    };

    return (
        <div className="container">
            <div className="row clearfix">
                <div className="col-lg-12">
                    <div className="card chat-app">
                        <div id="plist" className="people-list">
                            <div className="input-group">
                                <div className="input-group-prepend">
                                    <span className="input-group-text" onClick={handleClick}><FaSearch /></span>
                                </div>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Search..."
                                    value={searchQuery}
                                    onChange={handleChangeSearch}
                                    onKeyPress={handleKeyPress}
                                />
                            </div>
                            <div className="search-results-container">
                                <ul className="list-unstyled chat-list mt-2 mb-0">
                                    {chatList.map(chat => (
                                        <li className="clearfix" key={chat.id} onClick={() => handleChooseRoom(chat.id)}>
                                            <img src="https://bootdey.com/img/Content/avatar/avatar1.png" alt="avatar" />
                                            <div className="about">
                                                <div className="name">{chat.room_name}</div>
                                                <div className="status">
                                                    <FaCircle className={chat.status === 1 ? 'online' : 'offline'} /> {chat.status === 1 ? 'online' : `offline since ${chat.created_at}`}
                                                </div>
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                            <div className="search-results-container">
                                <ul className="search-results">
                                    {searchResults.length > 0 && searchResults.map(user => (
                                        <li key={user.id}>
                                            <div>Email: {user.email}</div>
                                            <div>Phone: {user.phone}</div>
                                        </li>
                                    ))}
                                </ul>

                            </div>
                        </div>
                        <div className="chat">
                            <div className="chat-header clearfix">
                                <div className="row">
                                    <div className="col-lg-6">
                                        <a href="#" data-toggle="modal" data-target="#view_info">
                                            <img src="https://bootdey.com/img/Content/avatar/avatar2.png" alt="avatar" />
                                        </a>
                                        <div className="chat-about">
                                            <h6 className="m-b-0">Aiden Chavez</h6>
                                            <small>Last seen: 2 hours ago</small>
                                        </div>
                                    </div>
                                    <div className="col-lg-6 hidden-sm text-right">
                                    <button className="btn btn-outline-info" onClick={() => handleShowRoomInfo(selectedChatId)}><FaCogs /></button>
                                        <a>             </a>
                                        <button className="btn btn-outline-warning" onClick={handleLogout}>< FaCircle />Logout</button>
                                        {roomInfo && (
                                            <div>
                                                <h2>Room Name: {roomInfo.room_name}</h2>
                                                <p>Status: {roomInfo.status}</p>
                                                <p>Members:</p>
                                                <ul>
                                                    {roomInfo.list_member.map(member => (
                                                        <li key={member.id}>
                                                            {member.username} - {member.email}
                                                            {/* Nút delete ở đây */}
                                                        </li>
                                                    ))}
                                                </ul>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </div>
                            <div className="chat-history">
                                <ListGroup style={{ maxHeight: '500px', overflowY: 'auto' }}>
                                    {messageList.map(message => (
                                        <ListGroup.Item key={message.id} className={`clearfix ${message.sender === user_id ? 'own-message' : 'other-message'}`}>
                                            <div className="message-data text-right">
                                                <span className="message-data-time">{message.formatted_time}</span>
                                                <img src="https://bootdey.com/img/Content/avatar/avatar7.png" alt="avatar" />
                                            </div>
                                            <div className={`message ${message.sender === user_id ? 'own-message' : 'other-message'}`}>
                                                {message.content}
                                            </div>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            </div>
                            <div className="chat-message clearfix">
                                <div className="input-group mb-0">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text" onClick={handleSendMessage}>
                                            <FaPaperPlane />
                                        </span>
                                    </div>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Enter text here..."
                                        value={message}
                                        onChange={handleMessageChange}
                                        onKeyPress={handleMessageKeyPress}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
