import React, { useEffect, useState } from 'react'
import './style.css'
import { Link } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'


function Login() {
    const [username, setUserName] = useState('')
    const [password, SetPassword] = useState('')
    const navigate = useNavigate();

    useEffect(()=>{
        let access_token = localStorage.getItem("access_token")
        if(access_token){
            navigate('/home');
        }
    },[])


    const handleLogin = async () => {
        try {
          if (!username || !password) {
            console.log("khong co tk mkKKKKKKKKKKKKKKKKKKKKKKKK");
            return;
          }
      
          const res = await axios.post("http://127.0.0.1:8000/api/login_user/", { 
            username: username, 
            password: password 
          });
          
          // Kiểm tra xem phản hồi từ server có chứa access_token không
          if (res && res.access_token) { // Dùng `res.access_token` thay vì `res.data.access_token`
            // Lưu access_token vào localStorage
            localStorage.setItem("access_token", res.access_token);
            console.log("Access token saved to localStorage:", res.access_token);
          } else {
            console.log("khon co data??????????????????");
          }
        } catch (error) {
          console.error("Error during login:", error);
          console.log("co loi");
        }
      };

    return (
        <div className='login template d-flex justify-content-center align-items-center vh-100 bg-primary'>
            <div className='form_container p-5 rounded bg-white'>
                <form>
                    <h3 className='text-center'>Sign In</h3>
                    <div className='mb-2'>
                        <label htmlFor='username'>Username</label>
                        <input type='text' placeholder='Enter Username' className='form-control' value={username} onChange={(event)=>setUserName(event.target.value)}></input>
                    </div>
                    <div className='mb-2'>
                        <label htmlFor='password'>Password</label>
                        <input type='password' placeholder='Enter Password' className='form-control' value={password} onChange={(event)=>SetPassword(event.target.value)}></input>
                    </div>
                    <div>
                        <input type='checkbox' className='custom-control custom-checkbox' id="check" />
                        <label htmlFor='check' className='custom-input-label ms-2'>
                            Remember me
                        </label>
                    </div>
                    <div className='d-grid'>
                        <button className='btn btn-primary' onClick={()=>handleLogin()}>Sign in</button>
                    </div>
                    <p className='text-end mt-2'>
                        Forgot <a href=''>Password?</a>
                        <Link to='/signup' className='ms-2'>Sign up</Link>
                    </p>
                </form>
            </div>
        </div>
    )
}

export default Login;
