import React, { useState, useEffect } from 'react';
import { FaPaperPlane, FaSearch, FaCircle } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ListGroup } from 'react-bootstrap';
import { jwtDecode } from 'jwt-decode';

// Import các components đã tạo
import Header from './Header';
import SearchBar from './SearchBar';
import ChatList from './ChatList';
import ChatHistory from './ChatHistory';
import MessageInput from './MessageInput';

function Home() {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResult] = useState([]);
    const [message, setMessage] = useState('');
    const access_token = localStorage.getItem('access_token');
    const user_id = access_token ? jwtDecode(access_token).user_id : null;

    const navigate = useNavigate();

    const handleMessageChange = (e) => {
        setMessage(e.target.value);
    };

    const handleSendMessage = async () => {
        if (!message.trim()) return; // Không gửi tin nhắn nếu nội dung là rỗng

        try {
            const response = await axios.post(
                'http://127.0.0.1:8000/api/chat/',
                {
                    room_id: selectedChatId,
                    content: message // Gửi nội dung tin nhắn
                },
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('access_token')}`
                    }
                }
            );
            console.log('Message sent:', response.data);
            // Xử lý khi tin nhắn được gửi thành công
        } catch (error) {
            console.error('Error sending message:', error.response.data);
            // Xử lý khi gặp lỗi khi gửi tin nhắn
        }

        // Sau khi gửi tin nhắn, xóa nội dung tin nhắn trong ô nhập liệu
        setMessage('');
    };

    const handleMessageKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    const handleSearch = async () => {
        try {
            // Tìm kiếm người dùng
        } catch (error) {
            console.error('Error searching user:', error);
        }
    };

    const handleChangeSearch = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    const handleLogout = () => {
        // Logout
    };

    return (
        <div className="container">
            <div className="row clearfix">
                <div className="col-lg-12">
                    <div className="card chat-app">
                        {/* Header */}
                        <Header handleLogout={handleLogout} />
                        <div id="plist" className="people-list">
                            {/* SearchBar */}
                            <SearchBar
                                searchQuery={searchQuery}
                                handleChangeSearch={handleChangeSearch}
                                handleKeyPress={handleKeyPress}
                                handleSearch={handleSearch}
                                searchResults={searchResults}
                            />
                            <ChatList user_id={user_id} />
                        </div>
                        <div className="chat">
                            <ChatHistory user_id={user_id} />
                            {/* MessageInput */}
                            <MessageInput
                                message={message}
                                handleMessageChange={handleMessageChange}
                                handleSendMessage={handleSendMessage}
                                handleMessageKeyPress={handleMessageKeyPress}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
